class Solution {
public:
    int maxOperations(string s) {
        int count1s = 0, output = 0;
        int n = s.length();

        for (int i=0;i<n;i++) {
            if (i == n - 1 && s[i] == '0') {
                output +=  count1s;
            }
            else if (i!= n - 1 && s[i] == '0' && s[i + 1] == '1') output += count1s; 
            else if (s[i] == '1') count1s++;

        }

        return output;
    }
};
