class Solution {
public:
    int maxOperations(string s) {
        int cnt  = 0;
        int n = s.size();
        vector<int> v(n, 0);
        int c0 = 0;
        for(int i = n-1; i>=0; i--) {
            if(s[i] == '1'){
                if(c0>0) v[i] = 1;
                // if(i<n-1) v[i] += v[i+1];
                c0 = 0;
            } else {
                c0++;
            }
            cout<<v[i]<<" ";
        }
        int curr = 0;
        for(int i = n-1; i>=0; i--) {
           if(s[i] == '1') { curr+=v[i];
            cnt+= curr;}
        }
        return cnt;
    }
};
