class Solution {
    public int maxOperations(String s) {
        int cnt = 0, a = 0;
        boolean pre = false;
        for(int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            if(c == '0') {
                if(!pre) {
                    cnt += a;
                    pre = true;
                }
            } else {
                a++;
                pre = false;
            }
        }
        return cnt;
    }
}
