class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector <int > sum(n + 1, 0);
        for (int i = n - 1; i >= 0; i -- )
        {
            int j = i;
            if (s[i] == '0')
            {
                while (s[j] == '0' && j > 0) j --; 
                sum[j] = 1;
            }
            i = j;
        }

        for (int i = n - 1; i >= 0; i -- )
            sum[i] += sum[i + 1];

        int res = 0;
        for (int i = 0; i < n; i ++ )
            if (s[i] == '1') res += sum[i];

        return res;
    }
};
