class Solution:
    def maxOperations(self, s: str) -> int:
        s = list(s)
        ones = 0
        operations = 0
        for i in range(len(s)):
            if s[i] == '1':
                ones += 1
            elif s[i - 1] == '1' and s[i] == '0':
                operations += ones
        return operations
