class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int x=0,y=0;
        int ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                x++;
                y=0;
            }
            else{
                y++;
                if(y==1) ans+=x;
            }
        }
        return ans;
    }
};
