class Solution {
public:
    int maxOperations(string s) {
        int res = 0, cnt = 0, n = s.size();
        for (int i = n - 1; i >= 0; ) {
            while (i >= 0 && s[i] == '0') i--;  // 跳过 0
            if (i >= 0 && i + 1 < n) cnt++;
            while (i >= 0 && s[i] == '1') i--, res += cnt;
        }
        return res;
    }
};
