class Solution {
    public int maxOperations(String s) {
        int zeroCount = 0; 
        int operations = 0;

        boolean flag=true;
        for (char c : s.toCharArray()) {
            if (c == '0'&& flag) {
                operations += zeroCount;
                flag=false;
            } else if (c == '1') {
                 zeroCount++;
                flag=true;
            }
        }

        return operations;
    }
}
