class Solution {
    public int maxOperations(String s) {
        char[] arr = s.toCharArray();
        int len = arr.length;

        int ones = 0;
        int ans = 0;

        for(int i = 0; i<len; i++) {
            if(arr[i] == '1') {
                ones++;
            } else {
                ans+= ones;
                while(i < len && arr[i] == '0') i++;
                i--;
            }
        }
        return ans;
    }
}
