class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int unos = 0;
        for (int i = 0; i + 1 < s.size(); i ++){
            if (s[i] == '1'){
                unos ++;
                if (s[i + 1] == '0'){
                    ans += unos;
                }
            }
        }
        return ans;
    }
};
