int pw(ll int a, ll int b, ll int m) {
    if(b==0) {
        return 1;
    }

    if(b%2 == 0) {
        ll int t = pw(a, (b/2), m);
        return (1ll*t*t)%m;
    }
    else {
        ll int t = pw(a, (b-1)/2, m);
        t = (1ll*t*t)%m;
        return (1ll*a*t)%m;
    }
}

const int N=500000;
const ll int mod = 1e9 + 7;

ll int fact[N], invfact[N];
void init() {
    ll int p = mod;
    fact[0]=1;
    int i;
    for(i=1; i<N; i++) {
        fact[i] = (i*fact[i-1])%p;
    }
    i--;
    invfact[i] = pw(fact[i], p-2, p);
    for(i--; i>=0; i--) {
        invfact[i] = (invfact[i+1]*(i+1))%p;
    }
}

int ncr(int n, int r) {
    return (((fact[n]*invfact[r])%mod)*invfact[n-r])%mod;
}

vector<int> sieve_of_eratosthenes(int n) {
    vector<int> prm;
    bool is_prime[n + 1];
    memset(is_prime, true, sizeof(is_prime));
    is_prime[0] = is_prime[1] = false;
    for(int p = 2; p * p <= n; p++) {
        if(is_prime[p]) {
            for(int i = p * p; i <= n; i += p) {
                is_prime[i] = false;
            }
        }
    }
    for(int i = 2; i <= n; i++) {
        if(is_prime[i]) {
            prm.pb(i);
        }
    }
    return prm;
}

class Solution {
public:
    int maxOperations(string s) {
        int c = 0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1') c++;
            else continue;
        }
        while(s.size()>0 && s.back()=='1'){
            s.pop_back();
            c--;
        }
        int n = s.size();
        int res = 0;
        for(int i=n-1;i>=0;){
            res += c;
            while(i>=0 && s[i]=='0'){
                i--;
            }
            while(i>=0 && s[i]=='1'){
                i--;
                c--;
            }
        }
        return res;
    }
};
