class Solution:
    def maxOperations(self, s: str) -> int:
        s = s + '1'
        n = len(s)
        ans = [0 for i in range(n)]

        zerolistcount = 0
        for i in range(n-1, -1, -1):
            if s[i] == '1':
                ans[i] = zerolistcount
            else:
                if i+1 < n and s[i+1] == '1':
                    zerolistcount += 1

        return sum(ans)
