class Solution {
public:
    int maxOperations(string s) {
        int count =0;
        int ones =0;
        int n = s.size();
        if(s[0]=='1')ones++;
        for(int i=1;i<n;i++){
            if(s[i]=='0'&&s[i-1]!='0'){
                count+=ones;
            }
            if(s[i]=='1'){
                ones++;
            }
        }
        return count;
    }
};
