class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int>pre(n+1,0);
        for (int i = n-2; i >=0; i--)
        {
            if(s[i] == '1' && s[i+1] == '0')
            {
                pre[i] = pre[i+1] + 1;
            }
            else
            {
                pre[i] = pre[i+1];
            }
        }
        // for(auto x : pre)
        // {
        //     cout<<x<<" ";
        // }
        // cout<<endl;
        int ans = 0;
        for (int i = 0; i < n; i++)
        {
            if(s[i] == '1')
            {
                ans += pre[i];
            }
        }
        return ans;


    }
};
