class Solution {
public:
    int maxOperations(string str) {
        int totalOps = 0, onesCount = 0;
        bool isPrevOne = true;
        for (int idx = 0; idx < str.size(); ++idx) {
            if (str[idx] == '1') {
                onesCount++;
                isPrevOne = true;
            } else {
                if (isPrevOne) totalOps += onesCount;
                isPrevOne = false;
            }
        }
        return totalOps;
    }
};
