class Solution:
    def maxOperations(self, s: str) -> int:
        ans=0
        zero=False
        one=0
        prev=[len(s),0]
        for i in range(len(s)-1,-1,-1):
            if s[i]=="0":
                zero=True
            else:
                if prev[0]<=len(s)-1-one:
                    prev=[i,prev[1]]

                else:
                    prev=[i,0]
                if zero:
                    prev[1]+=1
                    zero=False
                ans+=prev[1]
                one+=1

        return ans
