class Solution {
public:
    int maxOperations(string s) {
        while(!s.empty() && s.back()=='1')
        {
            s.pop_back();
        }
        int n=s.size();
        if(n==0) return 0;
        vector<int> vc1;
        for(int i=0;i<n;)
        {
            if(s[i]=='0')
            {
                i+=1;
                continue;
            }
            int j;
            for(j=i+1;j<n && s[j]=='1';++j);
            vc1.push_back(j-i);
            i=j;
        }
        vc1.push_back(0);
        // for(int i:vc1)
        // {
        //     cout<<i<<' ';
        // }
        // cout<<endl;
        int ans=0;
        for(int i=1;i<(int)vc1.size();++i)
        {
            ans+=vc1[i-1];
            vc1[i]+=vc1[i-1];
        }
        return ans;
    }
};
