class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        stack<char> st;

        int n = s.length();
        int i = 0;

        while(i<n){
            if(s[i] == '1'){
                st.push('1');
            }
            else{
                int j = i;
                while(j<n && s[j] == '0') j++;

                i = j-1;
                // cout<<st.size()<<endl;
                ans += (st.size());
            }
            i++;
        }
        return ans;
    }
};
