class Solution:
    def maxOperations(self, s: str) -> int:
        n1 = 0
        ans = 0

        i = 0
        while i < len(s):
            if s[i] == '1':
                n1 += 1
                i += 1
            else:
                ans += n1
                while i < len(s) and s[i] == '0': i += 1 

        return ans
