class Solution:
    def maxOperations(self, s: str) -> int:
        sit=list(s)
        n=len(sit)
        preo=0
        ct=0
        for i in range(n-1):
            if sit[i]=='1' and sit[i+1]!='0' :
                preo+=1
            elif sit[i]=='1' and sit[i+1]=='0':
                ct+=1
                ct=ct+preo
                preo+=1
        return ct
