func maxOperations(s string) int {
	cnts := make([]int, 0)
	tmp := 0

	for i := 0 ;i < len(s)-1;i++ {
		if s[i]	 == '1' {
			tmp++
		}
		if s[i+1] == '0' && tmp != 0 {
			cnts = append(cnts, tmp)
			tmp = 0
		}
	}

	ans := 0
	cnt := 0

	for _,c := range cnts {
		cnt = cnt + c
		ans = ans + cnt
	}
	return ans
}
