class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int till = 0;
        int n = s.size();
        for(int i=0; i<n-1; i++){
            if ((s[i]=='1')&&(s[i+1]=='0')){
                till++;
                ans+=till;
            }
            else if(s[i]=='1') till++;
        }
        return ans;
    }
};
