class Solution {
public:
    int maxOperations(string s) {
        int cn=0,sm=0,n=s.length();
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                sm+=cn;
                int j=i;
                while(j<n){
                    if(s[j]!='1') break;
                    j++;
                }
                cn+=(j-i);
                i=j-1;
            }
        }
        if(s[n-1]=='0'){
            sm+=cn;
        }
        return sm;
    }
};
