class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        ones = s.count("1")
        # print(zeros)
        n = len(s)
        r = n - 1
        while r >= 0:
            if s[r] == "1":
                r -= 1
                ones -= 1
            else:
                while s[r] == "0" and r >= 0:
                    r -= 1
                ans += ones
        return ans
