class Solution {
public:
    int maxOperations(string s) {
        string s_;
        int n = s.size();
        for (int i = 0; i < n; i++) {
            if (s_.empty()) {
                s_.push_back(s[i]);
            } else {
                if (s_.back() == s[i] && s[i] != '1') {
                    continue;
                } else {
                    s_.push_back(s[i]);
                }
            }
        }
        s = s_;
        reverse(s.begin(), s.end());
        n = s.size();
        long long ans = 0;
        int pref = 0;
        for (auto c : s) {
            if (c == '0') {
                pref++;
            } else {
                ans += pref;
            }
        }
        return ans;
    }
};
