class Solution {
public:
    int maxOperations(string s) 
    {
        int n = s.size(), res = 0;
        int i = 0, cnt = 0;


        while(i<n)
        {
            while(i<n && s[i]=='1')
            {
                cnt++;
                i++;
            }
            while(i<n && s[i]=='0')
            {
                i++;
                if(i==n)
                {
                    res += cnt;
                }
            }
            if(i<n && s[i]=='1')
            {
                res += cnt;
            }
        }

        return res;
    }
};
