class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ret = 0, last = -1, cnt = 0;
        for (int i = n - 1; i >= 0; i--) if (s[i] == '1') {
            if (last == -1) {
                cnt = (i < n - 1);
            } else if (i + 1 < last) {
                cnt++;
            }
            last = i;
            ret += cnt;
        }
        return ret;
    }
};
