class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int cur=0;
        int n=s.length();
        for(int i=0;i<n;i++){
             if(i>0&&s[i]=='1'&&s[i-1]=='0'){
               ans+=cur;
             }
            if(s[i]=='1'){
cur++;}
        }
        if(s[n-1]=='0'){
            ans+=cur;

        }
        return ans;

    }
};
