class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        std::vector<pair<char, int>> v;
        for (int i = 0; i < n; ++i) {
            if (v.empty() || v.back().first != s[i]) {
                v.emplace_back(s[i], 1);
            } else {
                v.back().second ++;
            }
        }
        std::vector<int> vv;
        for (int i = 0; i < v.size(); ++i) {
            if (v[i].first == '1') {
                vv.push_back(v[i].second);
            }
        }
        if (v.back().first == '1') vv.pop_back();
        int ret = 0;
        for (int i = 0; i < vv.size(); ++i) {
            ret += vv[i] * (vv.size() - i);
        }
        return ret;
    }
};
