class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
  int c=0,one=0,ans=0;
  for(int i=0;i<n;i++)
  {
    if(s[i]=='0')
    {
      c++;
    }
    else{
      if(c!=0)
        ans+=((one));
      c=0;
      one++;
    }
  }
  if(c!=0)
    ans+=((one));
  return ans;
    }
};
