class Solution {
public:
    int maxOperations(string s) {
        int c = 0, ans = 0;
        reverse(s.begin(),s.end());
        char p='a';
        for (char ch : s) {
            if (ch == '0'  && p!='0') {
                c++;
            } else if(ch=='1'){
                ans += c;
            }

            // cout<<ans<<"-"<<c<<" ";
            p=ch;
        }
        cout<<endl;
        return ans;
    }
};
