class Solution:
    def maxOperations(self, s: str) -> int:
        cnt1 = cur = ans = 0
        while cur < len(s):
            if s[cur] == '1':
                cnt1 += 1
                cur += 1
                continue
            while cur < len(s) and s[cur] == '0':
                cur += 1
            ans += cnt1
        return ans
