class Solution {
public:
    int maxOperations(string s) {
        int ans=0,p=0,r=0;
        bool a=0;

        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                p++;
                a=true;
            }
            else{
                if(a)ans+=p;
                a=false;
            }
        }
        return ans;
    }
};
