class Solution {
public:
    int maxOperations(string s) {
        string s1 = "";
        for(int i = 0 ; i < s.length(); i++)
        {
            if(s1.size() == 0)
                s1 += s[i];
            else
            {
                if(s[i] == '0' && s1.back() != '0')
                    s1 += s[i];
                else if(s[i] == '1')
                    s1 += s[i];
            }
        }
        int ans = 0,c = 0;
        for(int i = 0 ; i < s1.length() ; i++)
        {
            if(s1[i] == '0')
                ans += c;
            if(s1[i] == '1')
                c++;
        }
        return ans;
    }
};
