class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        while(s.size()>0&&s[s.size()-1]=='1') s.pop_back();
        int num=0;
        bool check=true;
        for(int i=s.size()-1;i>=0;i--){
            if(s[i]=='0'&&check){
                num+=1;
                check=false;
            }
            else if(s[i]=='1'){
                ans+=num;
                check=true;
            }
        }
        return ans;
    }
};
