class Solution {
public:
    int maxOperations(string s) {
         int cnt=0, ans=0;

        int n=s.size();
        if(s[n-1]=='0') cnt=1;

        for(int i=n-1;i>=0;i--)
        {
            if(s[i]=='0') ;

            else 
            {
                while(i>=0 && s[i]=='1')
                {
                    ans+= cnt;
                    i--;
                }
                cnt+=1;
            }
        }
        return ans;
    }
};
