class Solution:
    def maxOperations(self, s: str) -> int:
        S = [int(x) for x in s]
        arr = []
        for x in S:
            if x == 1:
                arr.append(x)
            else:
                if arr and arr[-1] == 0:
                    continue
                else:
                    arr.append(x)
        cnt = 0
        res = 0
        for x in arr:
            if x == 0:
                res += cnt
            else:
                cnt += 1        

        return res
