class Solution {
    public int maxOperations(String s) {
        int cnt = 0;
        int ans = 0;
        boolean f = true;
        for (char c: s.toCharArray()) {
            if (c == '1') {
                f = true;
                cnt++;
            } else {
                if (f) ans += cnt;
                f = false;
            }
        }
        return ans;
    }
}
