class Solution {
public:
    int maxOperations(string s) {
        // 从后往前看，贴着就是一样多
        int ans=0;
        int buf=0;
        for(int i=s.size()-2;i>=0;i--){
            if(s[i]=='0'){
                continue;
            }
            if(s[i+1]=='1'){
                ans+=buf;
            } else {
                buf++;
                ans+=buf;
            }
        }
        return ans;
    }
};
