class Solution {
public:
    int maxOperations(string s) {
        reverse(s.begin(), s.end());
        bool isPatch = false;
        int numPatch = 0;
        int res = 0;
        for(char ch: s){
            if(ch == '0'){
                if(!isPatch){
                    ++numPatch;
                    isPatch = true;
                }
            }
            else{
                res += numPatch;
                isPatch = false;
            }
        }
        return res;
    }
};
