class Solution {
public:
    int maxOperations(string s) {
        vector<int> blocks;
        int k = s.size()-1;
        while (k >= 0 && s[k] == '1') k--;
        while (k >= 0) {
            while (k >= 0 && s[k] == '0') k--;
            int i0 = k;
            while (k >= 0 && s[k] == '1') k--;
            blocks.push_back(i0-k);
        }
        int ans = 0;
        for (int i = 0; i < blocks.size(); i++) {
            ans += (i+1) * blocks[i];
        }
        return ans;
    }
};
