class Solution {

    /**
     * @param String $s
     * @return Integer
     */
    function maxOperations($s) {
        $length = strlen($s);
        $count = 0;
        $ans = 0;
        for ($index = 0; $index < $length; ++ $index) {
            if ($s[$index] == '0') {
                if ($index > 0 && $s[$index - 1] == '1') {
                    $ans += $count;
                }
            } else {
                ++ $count;
            }
        }

        return $ans;
    }
}
