class Solution {
public:
    int maxOperations(string s) {
        s+='1';
        ll n=s.length();
        ll c=0;
        ll ans=0;
        ll c0=0;
        for(ll i=0;i<n;i++)
        {
            if(s[i]=='1')
            {
                if(c0>0)
                    ans+=c;
                c++;
                c0=0;
            }
            else
            {
                c0++;
            }
        }

        return ans;
    }
};
