class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int ps = 0;
        bool hasOne = false;
        vector<int> vec;

        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                vec.push_back(1);
                hasOne = true;
            } else if (s[i] == '0' && hasOne) {
                vec.push_back(0);
                hasOne = false;
            }
        }

        for (int i = 0; i < vec.size(); i++) {
            if (vec[i] == 1) {
                ps++;
            } else {
                ans += ps;
            }
        }

        return ans;
    }
};
