class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int ans = 0;
        int zeros = 0;
        if (s.charAt(n-1)=='0') zeros++;
        for (int i=n-2; i>=0; i--) {
            if (s.charAt(i)=='0' && s.charAt(i+1)=='1') {
                zeros++;
            } else if (s.charAt(i)=='1') {
                ans+=zeros;
            }
        }
        return ans;
    }
}
