class Solution {
public:
    int maxOperations(string s) {
        int ones = 0;
        int n = s.length();

        int ans = 0;

        int i = 0;
        while(i<n){
            if(s[i]=='1'){
                ones++;
                i++;
            }
            else{
                ans = ans + ones;
                while(s[i]=='0'){
                    i++;
                }
            }
        }

        return ans;
    }
};
