class Solution {
    public int maxOperations(String s) {
        int cur = 0;
        int res = 0;
        for(int i = s.length() - 1; i >= 0; i--) {
            if(s.charAt(i) == '0' && (i == s.length() - 1 || s.charAt(i + 1) == '1')) cur++;
            else if(s.charAt(i) == '1') res += cur;
        }
        return res;
    }
}
