class Solution {
public:
    int maxOperations(string s) {

        int on=0;
        int ans=0;

        for(int i=0;i<s.size();i++){

            if(s[i]=='1')on++;
            else{
                if(i>0 && s[i-1]=='1'){
                    ans=ans+on;
                }

            }

        }
        return ans;
    }
};
