class Solution {
public:
    int maxOperations(string s) {
        const int n = s.size();
        int ans = 0, c = 0;
        for (int i = n - 1; i >= 0; --i) {
            if (s[i] == '0')  {
                int j = i;
                while (j - 1 >= 0 && s[j - 1] == '0') --j;
                i = j;
                ++c;
            } else {
                ans += c;
            }
        }
        return ans;
    }
};
