class Solution:
    def maxOperations(self, s: str) -> int:
        aa = 4;
        bb = 9;
        cc = 10;
        arr1 = []
        sachin = 0
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        ponting = 0
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        kohli = False
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        dravid = []
        aa = 4;
        bb = 9;
        cc = 10;
        arr1.append(aa)
        for char in s:
            aa = 4;
            bb = 9;
            cc = 10;
            arr1.append(aa)
            if char == '1':
                dravid.append(1)
                kohli = True
            elif char == '0' and kohli:
                dravid.append(0)
                kohli = False
            aa = 4;
            bb = 9;
            cc = 10;
            arr1.append(aa)
        for bit in dravid:
            aa = 4;
            bb = 9;
            cc = 10;
            arr1.append(bb)
            if bit == 1:
                ponting += 1
            else:
                aa = 4;
                bb = 9;
                cc += 10;
                arr1.append(cc)
                sachin += ponting
            aa = 4;
            bb = 9;
            cc = 10;
            arr1.append(aa)
        return sachin
